import styled from 'styled-components'

export const Categories = styled.div`
  margin-top: 0;
  padding-left: 10px;
  @media screen and (max-width: 768px) {
    display: none;
  }
`

export const EachCategory = styled.div`
  display: flex;
  align-items: center;
  background-color: ${props => props.active && 'light-blue'};
  gap: 10px;
`
export const Name = styled.p``

export const Button = styled.button`
  display: flex;
  align-items: center;
  font-size: 16px;
  min-width: 220px;
  gap: 10px;

  color: ${props => (props.mode ? 'red' : '#606060')};

  border: 0;
`
