import {Component} from 'react'

import Header from '../Header'
import Mode from '../Mode'

import {
  MainContainer,
  HomeContainer,
  PremiumContainer,
  PremiumLogo,
  AboutPremium,
  ImageCard,
  GetItNow,
  SearchContainer,
  SearchInput,
  SearchBtn,
  SearchIcon,
} from './styledComponents'

class Home extends Component {
  state = {dar: true, showPremiumCard: true}

  renderPremiumCard = () => {
    const {showPremiumCard} = this.state
    showPremiumCard && (
      <PremiumContainer>
        <ImageCard>
          <PremiumLogo
            src="https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-dark-theme-img.png"
            alt=""
          />
        </ImageCard>
      </PremiumContainer>
    )
  }

  render() {
    const {dar} = this.state
    return (
      <Mode.Consumer>
        {value => {
          const {darkMode} = value
          return (
            <MainContainer>
              <Header />
              <HomeContainer>
                {this.renderPremiumCard()}
                {this.renderVideosSection()}
              </HomeContainer>
            </MainContainer>
          )
        }}
      </Mode.Consumer>
    )
  }
}
export default Home
