import {Component} from 'react'

import {Link} from 'react-router-dom'

import {AiFillHome} from 'react-icons/ai'
import {HiFire} from 'react-icons/hi'
import {SiYoutubegaming} from 'react-icons/si'
import {MdPlaylistAdd} from 'react-icons/md'

import ModeContext from '../../context/ModeContext'

import {Categories, EachCategory, Name, Button} from './styledComponents'

const categoriesList = [
  {id: 'HOME', name: 'Home', path: '/', icon: <AiFillHome />},
  {id: 'TRENDING', name: 'Trending', path: '/trending', icon: <HiFire />},
  {id: 'GAMING', name: 'Gaming', path: '/gaming', icon: <SiYoutubegaming />},
  {
    id: 'SAVED_VIDEOS',
    name: 'Saved videos',
    path: '/saved-videos',
    icon: <MdPlaylistAdd />,
  },
]

class SideBar extends Component {
  state = {active: ''}

  onChangeTab = id => {
    this.setState({activeId: id})
  }

  render() {
    const {activeId} = this.state
    console.log(activeId)
    return (
      <ModeContext.Consumer>
        {value => {
          const {darkMode, activeTab, changeTab} = value
          const onChangeTab = event => {
            changeTab(event.target.value)
          }

          console.log(activeTab)

          return (
            <Categories mode={darkMode}>
              {categoriesList.map(eachCategory => (
                <EachCategory key={eachCategory.id}>
                  <Link to={eachCategory.path}>
                    <EachCategory
                      onClick={() => {
                        changeTab(eachCategory.id)
                      }}
                      active={activeTab === eachCategory.id}
                    >
                      {eachCategory.icon}
                      <Name active={activeTab}>{eachCategory.name}</Name>
                    </EachCategory>
                  </Link>
                </EachCategory>
              ))}
            </Categories>
          )
        }}
      </ModeContext.Consumer>
    )
  }
}
export default SideBar
