import {Component} from 'react'

import Cookies from 'js-cookie'

import VideoPlayer from '../VideoPlayer'
import Header from '../Header'

import {
  MainContainer,
  ReactPlayerContainer,
  DetailsContainer,
  Title,
  ViewsContainer,
  Views,
  Time,
  ReactionContainer,
  Like,
  Dislike,
  Save,
  Line,
  ChannelContainer,
  ChannelDetails,
  ChannelLogo,
  ChannelName,
  Subscribers,
  Description,
} from './styledComponents'

const apiConstants = {
  initial: 'INITIAL',
  success: 'SUCCESS',
  failure: 'FAILURE',
  inProgress: 'IN_PROGRESS',
}

class VideoItemDetails extends Component {
  state = {apiStatus: apiConstants.initial, videoData: []}

  componentDidMount() {
    this.getVideoItemDetailsApi()
  }

  getVideoItemDetailsApi = async () => {
    this.setState({apiStatus: apiConstants.inProgress})
    const {match} = this.props
    const {params} = match
    const {id} = params
    const token = Cookies.get('jwt_token')

    const url = `https://apis.ccbp.in/videos/${id}`
    const options = {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${token}`,
      },
    }
    const response = await fetch(url, options)
    const data = await response.json()

    const each = data.video_details
    if (response.ok) {
      const updatedData = {
        title: each.title,
        publishedAt: each.published_at,
        thumbnailUrl: each.thumbnail_url,
        videoUrl: each.video_url,
        viewCount: each.view_count,
        description: each.description,
        name: each.channel.name,
        profileImageUrl: each.channel.profile_image_url,
        subscriberCount: each.channel.subscriber_count,
      }

      this.setState({videoData: updatedData})
    } else {
      this.setState({apiStatus: apiConstants.failure})
    }
  }

  render() {
    const {videoData} = this.state
    const {
      description,
      name,
      title,
      profileImageUrl,
      publishedAt,
      subscriberCount,
      thumbnailUrl,
      videoUrl,
      viewCount,
    } = videoData

    return (
      <>
        <Header />
        <MainContainer>
          <ReactPlayerContainer>
            <VideoPlayer url={videoUrl} />
          </ReactPlayerContainer>
          <DetailsContainer>
            <Title>{title}</Title>
          </DetailsContainer>
        </MainContainer>
      </>
    )
  }
}
export default VideoItemDetails
