import styled from 'styled-components'

export const MainContainer = styled.div`
  background-color: ${props => (props.mode ? '#000000' : '#ffffff')};
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  max-width: 576px;
`

export const LoginLogo = styled.img`
  width: 140px;
  margin-top: 20px;
  margin-bottom: 40px;
`

export const Username = styled.div`
  display: flex;
  flex-direction: column;
  margin-bottom: 30px;
`

export const Password = styled(Username)`
  margin-bottom: 10px;
`

export const FormContainer = styled.form`
  display: flex;
  flex-direction: column;
  width: 80vw;
`

export const ShowPasswordContainer = styled.div`
  margin-bottom: 30px;
`

export const Input = styled.input`
  background-color: ${props => (props.mode ? '#ffffff' : '#000000')};
  height: 40px;
  border: 2px solid #f1f1f1;
  padding-left: 15px;
`

export const Label = styled.label`
  font-family: Roboto;
  font-weight: 500;
  margin-bottom: 5px;
  color: ${props => (props.mode ? '#ffffff' : '#64748b')};
`
export const CheckBox = styled.input``

export const ShowPassword = styled.label`
  color: ${props => (props.mode ? '#ffffff' : '#000000')};
`

export const LoginBtn = styled.button`
  background-color: #3b82f6;
  border: 0;
  color: #ffffff;
  height: 38px;
  border-radius: 5px;
  margin-bottom: 20px;
`
