import {Link} from 'react-router-dom'

import {AiFillHome} from 'react-icons/ai'
import {HiFire} from 'react-icons/hi'
import {SiYoutubegaming} from 'react-icons/si'
import {MdPlaylistAdd} from 'react-icons/md'

import ModeContext from '../../context/ModeContext'

import {Categories, EachCategory, Name} from './styledComponents'

const categoriesList = [
  {id: 'HOME', name: 'Home', path: '/', icon: <AiFillHome />},
  {id: 'TRENDING', name: 'Trending', path: '/trending', icon: <HiFire />},
  {id: 'GAMING', name: 'Gaming', path: '/gaming', icon: <SiYoutubegaming />},
  {
    id: 'SAVED_VIDEOS',
    name: 'Saved videos',
    path: '/saved-videos',
    icon: <MdPlaylistAdd />,
  },
]

const SideBar = () => (
  <ModeContext.Consumer>
    {value => {
      const {darkMode, activeTab, changeTab} = value

      const bgColor = darkMode ? '#ffffff' : 'red'

      return (
        <Categories mode={darkMode}>
          {categoriesList.map(eachCategory => (
            <Link to={eachCategory.path}>
              <EachCategory
                onClick={() => {
                  changeTab(eachCategory.id)
                }}
                active={activeTab === eachCategory.id}
                mode={darkMode}
                key={eachCategory.id}
                bgColor={bgColor}
              >
                {eachCategory.icon}
                <Name mode={darkMode} active={activeTab}>
                  {eachCategory.name}
                </Name>
              </EachCategory>
            </Link>
          ))}
        </Categories>
      )
    }}
  </ModeContext.Consumer>
)

export default SideBar
