import {Component} from 'react'

import {Redirect} from 'react-router-dom'
import Cookies from 'js-cookie'

import {
  MainContainer,
  FormContainer,
  LoginLogo,
  Username,
  Password,
  LoginBtn,
  Input,
  Label,
  ShowPasswordContainer,
} from './styledComponents'

class Login extends Component {
  state = {username: '', password: '', showPassword: false}

  changeUsername = event => {
    this.setState({username: event.target.value})
  }

  renderPassword = () => {
    const {password, showPassword} = this.state
    const passwordType = showPassword ? 'text' : 'password'
    return (
      <>
        <Label>PASSWORD</Label>
        <Input type="" value={password} onChange={this.changePassword} />
      </>
    )
  }

  renderUsername = () => {
    const {username} = this.state
    return (
      <>
        <Label>USERNAME</Label>
        <Input type="text" value={username} onChange={this.changeUsername} />
      </>
    )
  }

  render() {
    return (
      <MainContainer>
        <LoginLogo src="" alt="" />
        <Username>{this.renderUsername()}</Username>
        <Password>{this.renderPassword()}</Password>
        <ShowPasswordContainer>
          {this.renderShowPassword()}
        </ShowPasswordContainer>
        <LoginBtn type="submit">Login</LoginBtn>
      </MainContainer>
    )
  }
}
export default Login
