import {Link} from 'react-router-dom'

import {EachCategory, Name, Button} from './styledComponents'

const CategoryCard = props => {
  const {category, changeCategory, isActive} = props
  const {name, icon, id, path} = category

  const onClickBtn = () => {
    changeCategory(id)
  }
  console.log(isActive)
  return (
    <Link to={path}>
      <EachCategory active={isActive}>
        <Button onClick={onClickBtn} type="button">
          {icon}
          <Name>{name}</Name>
        </Button>
      </EachCategory>
    </Link>
  )
}
export default CategoryCard
