import {Component} from 'react'

import {Redirect} from 'react-router-dom'
import Cookies from 'js-cookie'

import {
  MainContainer,
  FormContainer,
  LoginLogo,
  Username,
  Password,
  LoginBtn,
  Input,
  Label,
  ShowPasswordContainer,
} from './styledComponents'

class Login extends Component {
  state = {username: '', password: '', showPassword: false}

  changeUsername = event => {
    this.setState({username: event.target.value})
  }

  changePassword = event => {
    this.setState({password: event.target.value})
  }

  showPassword = () => {
    this.setState({showPassword: !this.showPassword})
  }

  renderPassword = () => {
    const {password, showPassword} = this.state
    const passwordType = showPassword ? 'text' : 'password'
    return (
      <>
        <Label htmlFor="password">PASSWORD</Label>
        <Input
          id="password"
          type={passwordType}
          value={password}
          onChange={this.changePassword}
        />
      </>
    )
  }

  renderUsername = () => {
    const {username} = this.state
    return (
      <>
        <Label htmlFor="username">USERNAME</Label>
        <Input
          id="username"
          type="text"
          value={username}
          onChange={this.changeUsername}
        />
      </>
    )
  }

  renderShowPassword = () => (
    <>
      <Input type="checkBox" id="show" onClick={this.showPassword} />
      <Label htmlFor="show">Show Password</Label>
    </>
  )

  render() {
    return (
      <MainContainer>
        <LoginLogo
          src="https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-light-theme-img.png"
          alt=""
        />
        <Username>{this.renderUsername()}</Username>
        <Password>{this.renderPassword()}</Password>
        <ShowPasswordContainer>
          {this.renderShowPassword()}
        </ShowPasswordContainer>
        <LoginBtn type="submit">Login</LoginBtn>
      </MainContainer>
    )
  }
}
export default Login
