import {Component} from 'react'

import {AiFillHome} from 'react-icons/ai'
import {HiFire} from 'react-icons/hi'
import {SiYoutubegaming} from 'react-icons/si'
import {MdPlaylistAdd} from 'react-icons/md'
import Mode from '../Mode'
import CategoryCard from '../CategoryCard'

import {Categories} from './styledComponents'

const categoriesList = [
  {id: 'HOME', name: 'Home', path: '/', icon: <AiFillHome />},
  {id: 'TRENDING', name: 'Trending', path: '/trending', icon: <HiFire />},
  {id: 'GAMING', name: 'Gaming', path: '/gaming', icon: <SiYoutubegaming />},
  {
    id: 'SAVED_VIDEOS',
    name: 'Saved videos',
    path: '/saved-videos',
    icon: <MdPlaylistAdd />,
  },
]

class SideBar extends Component {
  state = {activeId: categoriesList[0].id}

  changeCategory = id => {
    this.setState({activeId: id})
  }

  render() {
    const {activeId} = this.state
    console.log(activeId)
    return (
      <Categories>
        {categoriesList.map(eachCategory => (
          <CategoryCard
            category={eachCategory}
            key={eachCategory.id}
            changeCategory={this.changeCategory}
            isActive={activeId === eachCategory.id}
          />
        ))}
      </Categories>
    )
  }
}
export default SideBar
