import {Component} from 'react'

import {AiFillHome} from 'react-icons/ai'
import {HiFire} from 'react-icons/hi'
import {SiYoutubegaming} from 'react-icons/si'
import {MdPlaylistAdd} from 'react-icons/md'

import CategoryCard from '../CategoryCard'

import {Categories} from './styledComponents'

const categoriesList = [
  {id: '1', name: 'Home', path: '/', icon: <AiFillHome />},
  {id: '2', name: 'Trending', path: '/trending', icon: <HiFire />},
  {id: '3', name: 'Gaming', path: '/gaming', icon: <SiYoutubegaming />},
  {
    id: '4',
    name: 'Saved videos',
    path: '/saved-videos',
    icon: <MdPlaylistAdd />,
  },
]

class SideBar extends Component {
  state = {activeId: categoriesList[0].name}

  render() {
    const {activeId} = this.state
    return (
      <Categories>
        {categoriesList.map(eachCategory => (
          <CategoryCard
            category={eachCategory}
            key={eachCategory.id}
            isActive={activeId === eachCategory.name}
          />
        ))}
      </Categories>
    )
  }
}
export default SideBar
