import {Component} from 'react'

import {Route, Switch} from 'react-router-dom'

import ModeContext from './context/ModeContext'
import Login from './components/Login'
import Home from './components/Home'
import Trending from './components/Trending'
import Gaming from './components/Gaming'
import SavedVideos from './components/SavedVideos'
import VideoItemDetails from './components/VideoItemDetails'
import NotFound from './components/NotFound'
import ProtectedRoute from './components/ProtectedRoute'

import './App.css'

class App extends Component {
  state = {darkMode: false, activeTab: 'HOME'}

  componentDidMount() {
    const getActiveMode = localStorage.getItem('mod')
    const getActiveTab = localStorage.getItem('tab')

    this.setState({getActiveMode, activeTab: getActiveTab})
  }

  setActiveTab = () => {
    const {activeTab} = this.state
    localStorage.setItem('tab', activeTab)
  }

  // setActiveMode = () => {
  //   const {darkMode} = this.state
  //   localStorage.setItem('mod', toString(darkMode))
  //  }

  changeMode = darkMode => {
    this.setState({darkMode}, this.setActiveMode)
    console.log(JSON.parse('true'))
  }

  changeTab = activeTab => {
    this.setState({activeTab}, this.setActiveTab)
  }

  render() {
    const {darkMode, activeTab} = this.state

    return (
      <ModeContext.Provider
        value={{
          darkMode,
          activeTab,
          changeTab: this.changeTab,
          changeMode: this.changeMode,
        }}
      >
        <Switch>
          <Route exact path="/login" component={Login} />
          <ProtectedRoute exact path="/" component={Home} />
          <ProtectedRoute exact path="/trending" component={Trending} />
          <ProtectedRoute exact path="/gaming" component={Gaming} />
          <ProtectedRoute exact path="/saved-videos" component={SavedVideos} />
          <ProtectedRoute
            exact
            path="/videos/:id"
            component={VideoItemDetails}
          />
          <Route component={NotFound} />
        </Switch>
      </ModeContext.Provider>
    )
  }
}

export default App
