import {Component} from 'react'

import {Redirect} from 'react-router-dom'
import Cookies from 'js-cookie'

import {
  MainContainer,
  FormContainer,
  LoginLogo,
  Username,
  Password,
  LoginBtn,
  Input,
  Label,
  ShowPasswordContainer,
  CheckBox,
  ShowPassword,
} from './styledComponents'

class Login extends Component {
  state = {darkMode: true, username: '', password: '', showPassword: false}

  changeUsername = event => {
    this.setState({username: event.target.value})
  }

  changePassword = event => {
    this.setState({password: event.target.value})
  }

  showPassword = () => {
    this.setState(prevState => ({showPassword: !prevState.showPassword}))
  }

  renderPassword = () => {
    const {password, showPassword} = this.state
    const passwordType = showPassword ? 'text' : 'password'
    return (
      <>
        <Label htmlFor="password">PASSWORD</Label>
        <Input
          id="password"
          placeholder="Password"
          type={passwordType}
          value={password}
          onChange={this.changePassword}
        />
      </>
    )
  }

  renderUsername = () => {
    const {username} = this.state
    return (
      <>
        <Label htmlFor="username">USERNAME</Label>
        <Input
          id="username"
          type="text"
          placeholder="Username"
          value={username}
          onChange={this.changeUsername}
        />
      </>
    )
  }

  renderShowPassword = () => (
    <>
      <CheckBox type="checkBox" id="show" onClick={this.showPassword} />
      <ShowPassword htmlFor="show">Show Password</ShowPassword>
    </>
  )

  render() {
    const {darkMode} = this.state
    return (
      <MainContainer mode={darkMode}>
        <LoginLogo
          src={
            darkMode
              ? 'https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-dark-theme-img.png'
              : 'https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-light-theme-img.png'
          }
          alt=""
        />
        <FormContainer>
          <Username>{this.renderUsername()}</Username>
          <Password>{this.renderPassword()}</Password>
          <ShowPasswordContainer>
            {this.renderShowPassword()}
          </ShowPasswordContainer>
          <LoginBtn type="submit">Login</LoginBtn>
        </FormContainer>
      </MainContainer>
    )
  }
}
export default Login
