import styled from 'styled-components'

export const Categories = styled.div`
  margin-top: 0;
  padding-left: 10px;
  background-color: ${props => props.mode && ' #313131'};
  @media screen and (max-width: 768px) {
    display: none;
  }
`
const bgColor = activeTab ? '#ffffff' : 'red'
export const EachCategory = styled.div`
  display: flex;
  align-items: center;
  color: ${props => (props.active ? 'red' : '#606060')};
  min-width: 220px;
  gap: 10px;
  background-colo: ${props => (props.active ? '#e2e8f0' : 'transparent')};
`
export const Name = styled.p`
  color: ${props => (props.active ? 'black' : '#606060')};
`

export const Button = styled.button`
  display: flex;
  align-items: center;
  font-size: 16px;

  gap: 10px;
  background-color: transparent;
  border: 0;
`
