import {Component} from 'react'

import {GrClose} from 'react-icons/gr'
import {AiOutlineSearch} from 'react-icons/ai'

import Header from '../Header'
import Mode from '../Mode'

import {
  MainContainer,
  HomeContainer,
  PremiumContainer,
  PremiumLogo,
  AboutPremium,
  ImageCard,
  GetItNow,
  SearchContainer,
  SearchInput,
  SearchBtn,
  VideosContainer,
} from './styledComponents'

class Home extends Component {
  state = {dar: true, showPremiumCard: true}

  closeBtn = () => {
    this.setState({showPremiumCard: false})
  }

  renderPremiumCard = () => {
    const {showPremiumCard} = this.state
    return (
      showPremiumCard && (
        <PremiumContainer>
          <ImageCard>
            <PremiumLogo
              src="https://assets.ccbp.in/frontend/react-js/nxt-watch-logo-light-theme-img.png"
              alt=""
            />
            <GrClose onClick={this.closeBtn} />
          </ImageCard>
          <AboutPremium>
            Buy Nxt Watch Premium prepaid plans with UPI
          </AboutPremium>
          <GetItNow>GET IT NOW</GetItNow>
        </PremiumContainer>
      )
    )
  }

  renderVideosSection = () => (
    <>
      <SearchContainer>
        <SearchInput type="text" />
        <SearchBtn type="button">
          <AiOutlineSearch />
        </SearchBtn>
      </SearchContainer>
    </>
  )

  render() {
    const {dar} = this.state
    return (
      <Mode.Consumer>
        {value => {
          const {darkMode} = value
          return (
            <MainContainer>
              <Header />
              <HomeContainer>
                {this.renderPremiumCard()}
                {this.renderVideosSection()}
              </HomeContainer>
            </MainContainer>
          )
        }}
      </Mode.Consumer>
    )
  }
}
export default Home
