import styled from 'styled-components'

export const Categories = styled.ul`
  min-width: 250px;
  color: red;
  margin-top: 0;
  padding-left: 10px;
  flex-grow: 1;
  background-color: ${props => props.mode && ' #313131'};
  @media screen and (max-width: 768px) {
    display: none;
  }
`
