import styled from 'styled-components'

export const Categories = styled.div`
  margin-top: 0;

  background-color: ${props => props.mode && ' #313131'};

  @media screen and (max-width: 768px) {
    display: none;
  }
`

export const EachCategory = styled.div`
  display: flex;
  align-items: center;
  background-color: ${props =>
    props.mode ? props.active && '#181818' : props.active && ' #cbd5e1'};
  gap: 10px;
  padding-left: 10px;
  color: ${props => (props.active ? 'red' : 'grey')};
  min-width: 220px;
`
export const Name = styled.p`
  color: ${props => (props.mode ? ' #ffffff' : 'grey')};
`

export const Button = styled.button`
  display: flex;
  align-items: center;
  font-size: 16px;

  gap: 10px;

  color: ${props => props.active && 'red'};
  border: 0;
`
