import {Component} from 'react'

import Header from '../Header'

import {HomeContainer} from './styledComponents'

class Home extends Component {
  state = {darkMode: true}

  changeMode = () => {
    this.setState(prevState => ({darkMode: !prevState.darkMode}))
  }

  render() {
    const {darkMode} = this.state
    return (
      <HomeContainer>
        <Header />
        <h1>Home</h1>
      </HomeContainer>
    )
  }
}
export default Home
