import styled from 'styled-components'

export const EachCategory = styled.li`
  display: flex;
  align-items: center;

  min-width: 220px;
  gap: 10px;
  background-color: ${props => (props.active ? '#e2e8f0' : 'transparent')};
`
export const Name = styled.p`
  color: ${props => (props.active ? 'black' : '#606060')};
`

export const Button = styled.button`
  display: flex;
  align-items: center;
  font-size: 16px;
  color: ${props => (props.active ? 'red' : '#606060')};
  gap: 10px;
  background-color: transparent;
  border: 0;
`
