import styled from 'styled-components'

export const EachCategory = styled.li`
  display: flex;
  align-items: center;
  gap: 10px;
`
export const Name = styled.p`
  colo: #606060;
`

export const Button = styled.button`
  display: flex;
  align-items: center;

  color: ${props => props.active && 'red'};
  gap: 10px;
  background-color: transparent;
  border: 0;
`
